INSERT INTO public.movies (title, description, genre, year, rating, duration, director, "cast", image_url) VALUES
('Dune: Parte Dois', 'Paul Atreides une-se a Chani e aos Fremen enquanto busca vingança contra os conspiradores que destruíram sua família.', 'Ficção Científica', 2024, 8.8, '166 min', 'Denis Villeneuve', ARRAY['Timothée Chalamet', 'Zendaya', 'Rebecca Ferguson'], '/dune-inspired-poster.png'),
('Oppenheimer', 'A história de J. Robert Oppenheimer e seu papel no desenvolvimento da bomba atômica durante a Segunda Guerra Mundial.', 'Drama', 2023, 8.4, '180 min', 'Christopher Nolan', ARRAY['Cillian Murphy', 'Emily Blunt', 'Robert Downey Jr.'], '/images/posters/oppenheimer-poster.png'),
('Homem-Aranha: Através do Aranhaverso', 'Miles Morales embarca em uma aventura épica através do multiverso com outros Homens-Aranha.', 'Animação', 2023, 8.7, '140 min', 'Joaquim Dos Santos', ARRAY['Shameik Moore', 'Hailee Steinfeld', 'Brian Tyree Henry'], '/spiderman-movie-poster.jpg'),
('Top Gun: Maverick', 'Depois de mais de 30 anos de serviço, Pete ''Maverick'' Mitchell continua sendo um dos melhores pilotos da Marinha.', 'Ação', 2022, 8.3, '130 min', 'Joseph Kosinski', ARRAY['Tom Cruise', 'Miles Teller', 'Jennifer Connelly'], '/top-gun-movie-poster.jpg'),
('Pantera Negra: Wakanda Para Sempre', 'A rainha Ramonda, Shuri, M''Baku, Okoye e as Dora Milaje lutam para proteger sua nação das potências mundiais.', 'Ação', 2022, 6.7, '161 min', 'Ryan Coogler', ARRAY['Letitia Wright', 'Angela Bassett', 'Tenoch Huerta'], '/black-panther-poster.jpg'),
('Tudo em Todo Lugar ao Mesmo Tempo', 'Uma dona de lavanderia chinesa-americana é varrida por uma aventura louca onde ela sozinha pode salvar o mundo.', 'Ficção Científica', 2022, 7.8, '139 min', 'Daniels', ARRAY['Michelle Yeoh', 'Stephanie Hsu', 'Ke Huy Quan'], '/everything-everywhere-movie-poster.jpg'),
('Avatar: O Caminho da Água', 'Jake Sully vive com sua nova família formada no planeta de Pandora.', 'Ficção Científica', 2022, 9.2, '192 min', 'James Cameron', ARRAY['Sam Worthington', 'Zoe Saldana', 'Sigourney Weaver'], '/generic-sci-fi-poster.png'),
('The Batman', 'Quando um assassino sádico aterroriza Gotham City, Batman deve investigar a corrupção oculta e questionar a ligação de sua família com o crime organizado.', 'Ação', 2022, 8.8, '176 min', 'Matt Reeves', ARRAY['Robert Pattinson', 'Zoë Kravitz', 'Paul Dano'], '/dark-knight-poster.png');
