INSERT INTO public.blog_posts (title, content, excerpt, category, featured, author_id) VALUES
('A Evolução dos Efeitos Especiais no Cinema', 'Desde os primórdios do cinema até os dias atuais, os efeitos especiais revolucionaram a forma como contamos histórias na tela grande. Uma análise profunda sobre como a tecnologia transformou a indústria cinematográfica...', 'Desde os primórdios do cinema até os dias atuais, os efeitos especiais revolucionaram a forma como contamos histórias na tela grande.', 'Tecnologia', true, NULL),
('Análise: Por que ''Dune'' Conquistou o Público', 'Uma análise detalhada dos elementos que fizeram de Dune um dos maiores sucessos da ficção científica moderna. Denis Villeneuve conseguiu adaptar o impossível...', 'Uma análise detalhada dos elementos que fizeram de Dune um dos maiores sucessos da ficção científica moderna.', 'Análise', false, NULL),
('Entrevista Exclusiva com Diretor Brasileiro', 'Conversamos com um dos diretores mais promissores do cinema nacional sobre seus próximos projetos. Em uma conversa exclusiva, o diretor revela seus planos...', 'Conversamos com um dos diretores mais promissores do cinema nacional sobre seus próximos projetos.', 'Entrevista', false, NULL),
('Os Melhores Filmes de 2024 Até Agora', 'Uma seleção cuidadosa dos filmes que marcaram o primeiro semestre de 2024 e que você não pode perder. 2024 tem sido um ano excepcional para o cinema...', 'Uma seleção cuidadosa dos filmes que marcaram o primeiro semestre de 2024 e que você não pode perder.', 'Lista', false, NULL),
('Como o Cinema Independente Está Mudando', 'O cinema independente passa por uma revolução com novas plataformas e formas de distribuição. As mudanças no mercado cinematográfico...', 'O cinema independente passa por uma revolução com novas plataformas e formas de distribuição.', 'Indústria', false, NULL),
('Bastidores: Como São Feitas as Cenas de Ação', 'Um olhar por trás das câmeras para entender como são criadas as sequências de ação mais espetaculares do cinema. As cenas de ação são uma das partes mais complexas...', 'Um olhar por trás das câmeras para entender como são criadas as sequências de ação mais espetaculares do cinema.', 'Bastidores', false, NULL);
